$(function(){var i={loading:!1,p:0,infinite:$(".weui-infinite-scroll"),loadData:function(){$.getJSON(listUrl,{page:i.p},function(i,n){if("success"===n){var a="";$.each(i,function(i,n){a+='<div class="weui_cells"><div class="icell"><div class="itime"><i class="icon-file-alt icon-2x"></i><span>'+formatDate(n.date)+'</span></div></div><div class="icell"><p>'+n.title+"</p></div></div>"}),$("#cp").append(a)}})}};i.loadData(),$(document.body).infinite().on("infinite",function(n){i.loading||(i.infinite.show(),i.loading=!0,setTimeout(function(){i.p++,i.loadData(i.p),i.loading=!1,i.infinite.hide()},1500))})});